     */
    function get_all_ingreso()
    {
        $ingreso = $this->db->query("
            SELECT
                *

            FROM
                `ingreso`

            WHERE
                1 = 1

            ORDER BY `ingreso_id` DESC
        ")->result_array();

        return $ingreso;
    }
        
