     */
    function update_ingreso($ingreso_id,$params)
    {
        $this->db->where('ingreso_id',$ingreso_id);
        return $this->db->update('ingreso',$params);
    }
    
    /*
     * function to delete ingreso
     */
    function delete_ingreso($ingreso_id)
    {
